import { useEffect, useState } from "react";
import "./App.css";

function Users() {
  const [user, setUser] = useState([]);

  const verify = (item) => {
    fetch("http://localhost:8081/verifyuser", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(item),
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json, "Name");
        if (json?.msg == "Email sent") {
          alert("user verified successfully");
          getuser();
        }
      });
  };

  const getuser = () => {
    fetch("http://localhost:8081/users")
      .then((res) => res.json())
      .then((json) => {
        if (json.length > 0) {
          setUser(json);
        }
      });
  };

  useEffect(() => {
    getuser();
  }, []);

  return (
    <div className="Users">
      <table>
        {user?.map((item, i) => {
          return (
            <tr>
              <td>{item.username}</td>
              <td> {item.email}</td>
              <td>
                {" "}
                {item.verify ? (
                  <div>Verified</div>
                ) : (
                  <button onClick={() => verify(item)}>Verify</button>
                )}
              </td>
            </tr>
          );
        })}
      </table>
    </div>
  );
}

export default Users;
